package ProblemaUm;import entities.Circle;import entities.Rectangle;import entities.Shape;import java.util.ArrayList;import java.util.List;public class problemaUm {    public static void problemaum(String[] args) {        List<Shape> myShapes = new ArrayList<>();        myShapes.add(new Rectangle(3.00, 2.00));        myShapes.add(new Circle(2.00));        List<Circle> myCircles = new ArrayList<>();        myCircles.add(new Circle(2.0));        myCircles.add(new Circle(3.0));        System.out.println("Total area: " + totalArea(myCircles));    }    public static double totalArea(List<? extends Shape> list) {        double sum = 0.0;        for (Shape s : list ) {            sum += s.area();        }        return sum;    }}